import React, { useContext, useState } from "react";
import { MyBooksContext } from "./HomeView/Home";
import * as BooksAPI from "./../BooksAPI";
const Menu = ({ title, options, book }) => {
  const setterBooks = useContext(MyBooksContext).changeBooks;
  const [Shelf, setShelf] = useState(book.shelf);

  const UpdateBookShelf = async () => {
    await BooksAPI.update(book, Shelf);
  };
  const onHandleChange = async (e) => {
    setShelf(e.target.value);
    await UpdateBookShelf();
    const res = await BooksAPI.getAll();
    setterBooks(res);
  };
  return (
    <select onChange={onHandleChange}>
      <option value="none" disabled>
        {title}
      </option>
      {options.map((option) => {
        return (
          <option
            key={option.id}
            value={option["value"]}
            selected={option.value === Shelf}
          >
            {option["text"]}
          </option>
        );
      })}
    </select>
  );
};

export default Menu;
