import React, { useContext, useState, useEffect } from "react";
import { MyBooksContext } from "./HomeView/Home";
import * as BooksAPI from "./../BooksAPI";
const Menu = ({ title, options, bookShelf }) => {
  const setterBooks = useContext(MyBooksContext).changeBooks;
  const [Shelf, setShelf] = useState(bookShelf);
  useEffect(() => {
    console.log(Shelf);
  });
  return (
    <select onChange={(e) => setShelf(e.target.value)}>
      <option value="none" disabled>
        {title}
      </option>
      {options.map((option) => {
        return (
          <option
            key={option.id}
            value={option["value"]}
            onClick={() => {
              setShelf(option.value);
            }}
          >
            {option["text"]}
          </option>
        );
      })}
    </select>
  );
};

export default Menu;
