import React, { useContext } from "react";
import Menu from "./Menu";
import { MyBooksContext } from "../App";
const Book = ({ destination, book }) => {
  const myBooks = useContext(MyBooksContext).myBooks;
  const isUsed = myBooks.filter((Fbook) => Fbook.id === book.id);
  const MenueOption1 = [
    {
      id: 1,
      value: "currentlyReading",
      text: "Currently reading",
    },
    {
      id: 2,
      value: "wantToRead",
      text: "Want to Read",
    },
    {
      id: 3,
      value: "read",
      text: "Read",
    },
    {
      id: 4,
      value: "none",
      text: "None",
    },
  ];
  return (
    <li>
      <div className="book">
        <div className="book-top">
          <div
            className="book-cover"
            style={{
              width: 128,
              height: 193,
              backgroundImage: `url(${book.imageLinks.thumbnail})`,
            }}
          ></div>
          <div className="book-shelf-changer">
            <Menu
              title={Boolean(isUsed.length) ? "Move to ..." : "Add to ..."}
              options={MenueOption1}
              book={book}
            />
          </div>
        </div>
        <div className="book-title">{book.title}</div>
        <div className="book-authors">
          {book.authors ? book.authors.join(",") : "Unknown"}
        </div>
      </div>
    </li>
  );
};

export default Book;
