import React from "react";

const Menu = ({ title, options }) => {
  return (
    <select>
      <option value="none" disabled>
        {title}
      </option>
      {options.map((option) => {
        return <option value={option["value"]}>{option["text"]}</option>;
      })}
    </select>
  );
};

export default Menu;
