import React, { useContext } from "react";
import { MyBooksContext } from "./HomeView/Home";
const Menu = ({ title, options }) => {
  const booksSetter = useContext(MyBooksContext).setMyBooks;

  return (
    <select>
      <option value="none" disabled>
        {title}
      </option>
      {options.map((option) => {
        return (
          <option key={option.id} value={option["value"]}>
            {option["text"]}
          </option>
        );
      })}
    </select>
  );
};

export default Menu;
