import {
  createContext,
  useContext,
  useReducer,
} from "react/cjs/react.production.min";

export const DataLayerContext = createContext();

export const DataLayerComponent = ({ init, reducer, children }) => {
  <DataLayerContext.Provider value={useReducer(reducer, init)}>
    {children}
  </DataLayerContext.Provider>;
};

