import React, { useState, useEffect } from "react";
import * as BooksAPI from "../../BooksAPI";
import Shelf from "./Shelf";
import { Link } from "react-router-dom";

export const MyBooksContext = createContext();
const Home = () => {
  const [myBooks, setMyBooks] = useState([]);
  const [Loading, setLoading] = useState(false);
  const getMyBooks = async () => {
    setLoading(true);
    const res = await BooksAPI.getAll();
    setMyBooks(res);
    setLoading(false);
  };
  useEffect(() => {
    getMyBooks();
  }, []);
  return (
    <div className="list-books">
      <div className="list-books-title">
        <h1>MyReads</h1>
      </div>
      {Loading ? (
        <h1 style={{ textAlign: "center", margin: "15px auto" }}>
          Loading . . .
        </h1>
      ) : (
        <div className="list-books-content">
          <MyBooksContext.Provider
            value={{
              myBooks,
              getBooks: () => {
                getMyBooks();
              },
            }}
          >
            <div>
              <Shelf title="Currently Reading" shelfID="currentlyReading" />
              <Shelf title="Read" shelfID="read" />
              <Shelf title="Want to read" shelfID="wantToRead" />
            </div>
            <div className="open-search">
              <Link to="search">Add a book</Link>
            </div>
          </MyBooksContext.Provider>
        </div>
      )}
    </div>
  );
};

export default Home;
